mapper.select:
	select 
	f.id, father_name, father_mobile, father_age, mother_name, mother_mobile, mother_age, 
  other_name, other_mobile, other_age, child_name, child_mobile, child_age, child_img, 
  child_title, child_comment, f.activity_id, f.marine_id, f.creater, 
  f.create_time,a.activity_name,a.activity_num, a.activity_type, m.marine_name
	from t_activity_family f 
	inner join t_activity_info a on a.is_delete = 'N' and f.activity_id = a.id
	inner join t_activity_marines m on m.id = f.marine_id
  where f.is_delete='N'
  
  	and f.activity_id = #{params.activityId,jdbcType=INTEGER}
  
  
  	and f.child_name = #{params.childName,jdbcType=VARCHAR}
  
  
  	and a.activity_type = #{params.activityType,jdbcType=VARCHAR}
  
  
  	and a.activity_name like concat('%',#{params.activityName,jdbcType=VARCHAR},'%')
  
  
  	and DATE_FORMAT(a.start_date,'%Y-%m-%d') >= #{params.startDate,jdbcType=VARCHAR}
  
  
  	and DATE_FORMAT(a.start_date,'%Y-%m-%d')  <=  #{params.endDate,jdbcType=VARCHAR}
  
  order f.id desc
