mapper.select:
    SELECT ,IFNULL(enter_peoples,0) enter_peoples 
    FROM t_activity_info a
	LEFT JOIN (
	  SELECT COUNT(1) enter_peoples, activity_id FROM t_activity_family WHERE is_delete = 'n' GROUP BY activity_id
	) f ON a.id = f.activity_id
	WHERE a.is_delete = 'N'

    	and activity_type = #{params.activityType,jdbcType=VARCHAR}


    	and activity_name like concat('%',#{params.activityName,jdbcType=VARCHAR},'%')


    	and DATE_FORMAT(start_date,'%Y-%m-%d') >= #{params.startDate,jdbcType=VARCHAR}


    	and DATE_FORMAT(start_date,'%Y-%m-%d')  <=  #{params.endDate,jdbcType=VARCHAR}
