mapper.select:
	select 
	
  from t_hollow_man_info
  where is_delete = 'N' and openid = #{openid,jdbcType=VARCHAR}

	SELECT 
  
  FROM t_hollow_man_info
  WHERE is_delete = 'N' AND hm_name = #{hmName,jdbcType=VARCHAR}
  AND hm_mobile = #{hmMobile,jdbcType=VARCHAR}

  	SELECT h.*,IFNULL(take_num,0) take_num FROM t_hollow_man_info h
	LEFT JOIN (
	  SELECT COUNT(1) take_num, hm_id FROM t_activity_hm_sign 
	  WHERE is_delete = 'N' AND is_effect=1 
	  GROUP BY hm_id
	)n ON n.hm_id = h.id
	
		hm_name = #{params.hmName,jdbcType=VARCHAR}
	
	
		hm_mobile = #{params.hmMobile,jdbcType=VARCHAR}
	
	
		id_card = #{params.idCard,jdbcType=VARCHAR}
	
	
		sex = #{params.sex,jdbcType=VARCHAR}
	
	
		is_active = #{params.isActive,jdbcType=INTEGER}
	
	order by creater desc
